import { Didact, DidactDOM } from './Didact';
// import React, { ReactDOM } from 'react';
// import ReactDOM from 'react-dom';
class Welcome extends Didact.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  // componentDidMount() {
  //   setTimeout(() => {
  //     this.setState({
  //       count: 1,
  //     });
  //   }, 1000);
  // }
  onClick = () => {
    for (let i = 0; i < 10; i++) {
      this.setState(prevState => {
        return {
          count: prevState.count + 1,
        };
      });
    }
  };
  render() {
    return (
      <div
        onClick={this.onClick}
        className="welcome"
        style={{ height: '100px', backgroundColor: 'red' }}
      >
        <Hello name={this.state.count + 1} />
        <Hello>hhhh</Hello>
        {/* {this.state.count < 9 && <Hello name={this.state.count + 1} />} */}
        {/* welcome: {this.state.count} */}
        {/* {this.state.count < 20 && <div>test</div>} */}
      </div>
    );
  }
}
const App = () => (
  <div className="app" key="app" style={{ backgroundColor: 'red' }}>
    <p>11</p>
    <p>22</p>
    <Welcome />
  </div>
);

// class Hello extends Didact.Component {
//   render() {
//     return <div>hello world {this.props.name}</div>;
//   }
// }
let name = 1;
function handleClick() {
  name += 1;
  DidactDOM.render(Hello(), rootDom);
}
const Hello = ({ name, children }) => {
  return (
    <div>
      hello world {name}
      {name > 2 && 'hah'}
      {name < 5 && 'eee'}
      {children}
    </div>
  );
};

const rootDom = document.getElementById('root');
// DidactDOM.render(<Welcome />, rootDom);

// ReactDom.render(App, root);

// 1.render方法
// - jsx和vdom： babel调用createElement(设置babel)
// 2. 组件

// function tick() {
//   const time = new Date().toLocaleTimeString();
//   const clockElement = <h1>{time}</h1>;
//   DidactDOM.render(clockElement, rootDom);
// }
// tick();
// setInterval(tick, 1000);

const randomLikes = () => Math.ceil(Math.random() * 100);

const stories = [
  {
    name: 'Didact introduction',
    url: 'http://bit.ly/2pX7HNn',
    likes: randomLikes(),
  },
  {
    name: 'Rendering DOM elements ',
    url: 'http://bit.ly/2qCOejH',
    likes: randomLikes(),
  },
  {
    name: 'Element creation and JSX',
    url: 'http://bit.ly/2qGbw8S',
    likes: randomLikes(),
  },
  {
    name: 'Instances and reconciliation',
    url: 'http://bit.ly/2q4A746',
    likes: randomLikes(),
  },
  {
    name: 'Components and state',
    url: 'http://bit.ly/2rE16nh',
    likes: randomLikes(),
  },
];

const appElement = () => (
  <div>
    <ul>{stories.map(storyElement)}</ul>
  </div>
);

function storyElement(story) {
  return (
    <li>
      <button onClick={e => handleClick(story)}>
        {story.likes}
        <b>❤️</b>
      </button>
      <a href={story.url}>{story.name}</a>
    </li>
  );
}

function handleClick(story) {
  story.likes += 1;
  DidactDOM.render(appElement(), document.getElementById('root'));
}

DidactDOM.render(<Hello>hhh</Hello>, document.getElementById('root'));
